import './Header.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faHouse } from '@fortawesome/free-solid-svg-icons';
import { faComments } from '@fortawesome/free-solid-svg-icons';

function Header() {
    var menuIcon = document.getElementById('menuIcon');
    var menuMobUl = document.getElementById('menuMobUl');
    
    menuIcon.addEventListener('click', () => {
        if(menuMobUl.classList.contains('show')) {
            menuMobUl.classList.remove('show');
        } else {
            menuMobUl.classList.add('show');
        }
    })

    return (
        <div className="Header">
            <div className='inner_width'>
                <h2>QuizzApp</h2>
                <div id='menuIcon' className='menu_icon'>
                    <FontAwesomeIcon icon={faBars} className='faBars' />
                </div>
            </div>
            <nav className='navBar'>
                <ul>
                    <li><a href='/'>Home</a></li>
                    <li><a href='#'>em breve...</a></li>
                    <li><a href='#'>Feedback</a></li>
                    <li><a href='#'>em breve...</a></li>
                </ul>
            </nav>
            <div className='navBar_mob'>
                <nav>
                    <ul id='menuMobUl'>
                        <li><a href='/'><FontAwesomeIcon icon={faHouse} className='faHouse' /></a></li>
                        <li><a href='#'>em breve...</a></li>
                        <li><a href='#'><FontAwesomeIcon icon={faComments} className='faComments' /></a></li>
                        <li><a href='#'>em breve...</a></li>
                    </ul>
                </nav>
            </div>

        </div>
    );
}

export default Header;
