import './quizz01.css';
import Aside from './Aside/Aside';

function Quizz01() {
    const progressBar = document.getElementById('quizz_mainContent_progress_bar');
    const progressNext = document.getElementById('progress_next');
    const progressPrev = document.getElementById('progress_prev');
    const steps = document.querySelectorAll('.step');
    let active = 1;

    function updateProgress() {
        steps.forEach((step, i) => {
            if(i < active) {
                step.classList.add('active');
            } else {
                step.classList.remove('active');
            }
        });

        progressBar.style.width = ((active - 1) / (steps.length - 1)) * 100 + "%";

        if(active === 1) {
            progressPrev.disabled = true;
        } else if(active === steps.length) {
            progressNext.disabled = true;
        } else {
            progressPrev.disabled = false;
            progressNext.disabled = false;
        }
    };

    function nextStep() {
        active++;
        if(active > steps.length) {
            active = steps.length;
        }
        updateProgress();
    };

    function prevStep() {
        active--;
        if(active < steps.length) {
            active = steps.length;
        }
        updateProgress();
    };

    return (
        <div className='Quizz01'>
            <Aside />
            <main className='quizz_frontMain'>
                <div className='quizz_frontContent'>
                    <div className='quizzTitle_cont'>
                        <img src='https://images8.alphacoders.com/479/479393.jpg' alt='wallpaper minecraft' />
                        <h2>Titulo do Quizz</h2>
                    </div>
                    <div className='quizzContentDetails_cont'>
                        <div className='quizzDescription_cont'>
                            <p>
                                Descrição do quizz descrição do quizz descrição do quizz
                                descrição do quizz descrição do quizz descrição do quizz
                                descrição do quizz descrição do quizz descrição do quizz
                                descrição do quizz descrição do quizz descrição do quizz
                                descrição do quizz descrição do quizz descrição do quizz
                                descrição do quizz descrição do quizz descrição do quizz
                                descrição do quizz descrição do quizz descrição do quizz
                            </p>
                        </div>
                        <div className='quizzBtnStart_cont'>
                            <button id='btnStart' className='quizzBtn_start' type='button'>começar o quizz</button>
                        </div>
                    </div>
                </div>
                <section className='quizz_mainContent'>
                    <div className='quizz_mainContent_title'>
                        <div className='quizz_mainContent_progress_cont' id='quizz_mainContent_progress_cont'>
                            
                        </div>
                        <img src='https://images8.alphacoders.com/479/479393.jpg' alt='wallpaper minecraft' />
                        <h2>Pergunta do Quizz</h2>
                    </div>
                    <div className='quizz_mainContent_options'>
                        <form>
                            <label htmlFor='option1'>Opção 1</label>
                            <input type="radio" id='option1' name='option1' />
                            <label htmlFor='option2'>Opção 2</label>
                            <input type="radio" id='option2' name='option2' />
                            <label htmlFor='option3'>Opção 3</label>
                            <input type="radio" id='option3' name='option3' />
                            <label htmlFor='option4'>Opção 4</label>
                            <input type="radio" id='option4' name='option4' />
                            <label htmlFor='option5'>Opção 5</label>
                            <input type="radio" id='option5' name='option5' />
                        </form>
                        <div className='quizz_mainContent_button'>
                            <button id='progress_prev' onClick={prevStep} disabled>Voltar</button>
                            <button id='progress_next' onClick={nextStep}>Próximo</button>
                        </div>
                    </div>
                </section>
            </main>
            <Aside />
        </div>
    )
}

export default Quizz01;
