import './Css/feedback.css';
import React from 'react';
import Select from 'react-select';
import Aside from '../Quizz - 01/Aside/Aside';
import Main from '../Main/Main';

function Feedback() {
    
    return (
        <div className='Feedback'>
            <Aside />
            <Main>
                <div className='feedback_content'>
                    <h1>Conte-nos a sua opinião!</h1>
                    <form method='post' className='form_cont'>
                        <div className='form_name'>
                            <input type='text' placeholder='Nome...' />
                            <input type='text' placeholder='Sobrenome...' />
                        </div>{/* form_name */}
                        <div className='form_info'>
                            <input type='email' placeholder='Seu email' />
                                <Select
                                    styles={{
                                        control: (baseStyles, state) => ({
                                            ...baseStyles,
                                            borderColor: state.isFocused ? 'red' : 'pink',
                                        }),
                                    }}
                                />    
                            <textarea placeholder='Menssagem'></textarea>
                        </div>{/* form_info */}
                        <div className='form_gender'>
                            <p>Como você se identifica?</p>
                            <div className='form_gender_inputCont'>
                                <input id='masculino' type='radio' name='genero' />
                                <label htmlFor='masculino'></label>
                                <span>Masculino</span>
                            </div>{/* form_gender_inputCont */}
                            <div className='form_gender_inputCont'>
                                <input id='feminino' type='radio' name='genero' />
                                <label htmlFor='feminino'></label>
                                <span>Feminino</span>
                            </div>{/* form_gender_inputCont */}
                            <div className='form_gender_inputCont'>
                                <input id='nenhum' type='radio' name='genero' />
                                <label htmlFor='nenhum'></label>
                                <span>Nenhum</span>
                            </div>{/* form_gender_inputCont */}
                        </div>{/* form_gender */}
                        <div className="form_birthday">
                            <p>Quando você nasceu?</p>
                            <div className='form_birthday_inputCont'>
                                <select id='day'></select>
                            </div>{/* form_birthday_inputCont */}
                            <div className='form_birthday_inputCont'>
                                <select id='month'></select>
                            </div>{/* form_birthday_inputCont */}
                            <div className='form_birthday_inputCont'>
                                <select id='year'></select>
                            </div>{/* form_birthday_inputCont */}
                        </div>{/* form_birthday */}
                        <div className='button_submit_cont'>
                            <input type='submit' name='action' value={'Enviar'} />
                        </div>{/* button_submit_cont */}
                    </form>{/* form_cont */}
                </div>{/* feedback_content */}
            </Main>
            <Aside />
        </div>
    )
}

export default Feedback;
