                questions[6].classList.remove('question_current');
                questions[6].classList.add('question');
                questions[7].classList.remove('question');
                questions[7].classList.add('question_current');
            }
            else if(questions[7].classList.contains('question_current')) {
                questions[7].classList.remove('question_current');
                questions[7].classList.add('question');
                questions[8].classList.remove('question');
                questions[8].classList.add('question_current');
            }
            else if(questions[8].classList.contains('question_current')) {
                questions[8].classList.remove('question_current');
                questions[8].classList.add('question');
                questions[9].classList.remove('question');
                questions[9].classList.add('question_current');
            }
